{"class":"","cache":"1","html_content":"<div class="box-contact-page">
    {if !empty($data_extend['locale'][{LANGUAGE}]['tieu_de'])}
    	<div class="slogan-section text-center">
    		{$this->Block->getLocale('tieu_de', $data_extend)}
    	</div>
    {/if}
    {if !empty($data_extend['locale'][{LANGUAGE}]['mo_ta'])}
    	<div class="dsc-slogan text-center">
    		{$this->Block->getLocale('mo_ta', $data_extend)}
    	</div>
    {/if}
    <div class="info">
        <div class="row">
            <div class="col-xl-3 col-6">
                <div class="item-contact">
                    <div class="icon">
                    	{$this->LazyLoad->renderImage([
                    		'src' => "{if !empty($data_extend['locale'][{LANGUAGE}]['icon'])}{$this->Utilities->replaceVariableSystem($this->Block->getLocale('icon', $data_extend))}{/if}", 
                    		'alt' => "{if !empty($data_extend['locale'][{LANGUAGE}]['tieu_de'])}{$this->Block->getLocale('tieu_de', $data_extend)}{/if}",
                    		'class' => 'img-fluid',
                    		'ignore'=> 'true'
                    	])}
                    </div>
                    {if !empty($data_extend['locale'][{LANGUAGE}]['name_item_01'])}
                    	<div class="name">
                    		{$this->Block->getLocale('name_item_01', $data_extend)}
                    	</div>
                    {/if}
                    {if !empty($data_extend['locale'][{LANGUAGE}]['dsc_item_01'])}
                    	<div class="dsc">
                    		{$this->Block->getLocale('dsc_item_01', $data_extend)}
                    	</div>
                    {/if}
                    {if !empty($data_extend['locale'][{LANGUAGE}]['phone'])}
                        <div class="phone">
                            <a class="btn-all" href="tel:{$this->Block->getLocale('phone', $data_extend)}">
                                {$this->Block->getLocale('phone', $data_extend)}
                            </a>
                        </div>
                    {/if}
                </div>
            </div>
            
            <div class="col-xl-3 col-6">
                <div class="item-contact">
                    <div class="icon">
                    	{$this->LazyLoad->renderImage([
                    		'src' => "{CDN_URL}/media/icon/paper.webp", 
                    		'alt' => "{if !empty($data_extend['locale'][{LANGUAGE}]['tieu_de'])}{$this->Block->getLocale('tieu_de', $data_extend)}{/if}",
                    		'class' => 'img-fluid',
                    		'ignore'=> 'true'
                    	])}
                    </div>
                    {if !empty($data_extend['locale'][{LANGUAGE}]['name_item_02'])}
                    	<div class="name">
                    		{$this->Block->getLocale('name_item_02', $data_extend)}
                    	</div>
