                            <a class="btn-all" href="{$this->Block->getLocale('link_start_now', $data_extend)}">
                                <i class="fa-brands fa-telegram mr-3"></i>
                                Telegram 
                            </a>
                        </div>
                    {/if}
                </div>
            </div>
            <div class="col-xl-6 col-12">
                <div class="item-contact item-contact-03">
                    {if !empty($data_extend['locale'][{LANGUAGE}]['quick_contact'])}
                    	<div class="name font-weight-bold">
                    		{$this->Block->getLocale('quick_contact', $data_extend)}
                    	</div>
                    {/if}
                    
                    <ul class="pl-0">
                        {assign website_info value = $this->Setting->getWebsiteInfo()}
                        {if !empty($data_extend['locale'][{LANGUAGE}]['link_start_now'])}
                        	<li>
                                <a class="color-highlight" href="{$this->Block->getLocale('link_start_now', $data_extend)}">
                                    <i class="fa-brands fa-telegram mr-3"></i>
                                    Telegram 
                                </a>
                            </li>
                        {/if}
                        {if !empty($website_info.email)}
                        	<li>
                                <a href="mailto:{$website_info.email}">
                                    <i class="iconsax isax-sms mr-3"></i>
                                    {$website_info.email}
                                </a>
                            </li>
                        {/if}
                    </ul>
                    <div class="hours">
                        {if !empty($data_extend['locale'][{LANGUAGE}]['title_hours'])}
                        	<div class="name font-weight-bold">
                        		{$this->Block->getLocale('title_hours', $data_extend)}
                        	</div>
                        {/if}
                        {if !empty($data_extend['locale'][{LANGUAGE}]['hours'])}
                            <p>
                                {$this->Block->getLocale('hours', $data_extend)}
                            </p>
                        {/if}
                    </div>
                    
                    {if !empty($data_extend['locale'][{LANGUAGE}]['whatsapp'])}
                    	<div class="whatsapp font-weight-bold">
                    		<a href="{$this->Block->getLocale('whatsapp', $data_extend)}">
                    		    <i class="fa-brands fa-telegram mr-3"></i>
                                Telegram 
                    		</a>
                    	</div>
                    {/if}
                    
                </div>
            </div>
        </div>
    </div>
</div>"}

