{"class":"","cache":"1","html_content":"{if !empty($data_extend.data_collection[{LANGUAGE}])}
    <div class="box-top-deals">
        <div class="title-link">
            {if !empty($data_extend['locale'][{LANGUAGE}]['tieu_de'])}
                <h3 class="title-section">
                    {$this->Block->getLocale('tieu_de', $data_extend)}
                </h3>
            {/if}
            {if !empty($data_extend['locale'][{LANGUAGE}]['link'])}
                <a href="{$this->Block->getLocale('link', $data_extend)}" class="link-right">
                    {if !empty($data_extend['locale'][{LANGUAGE}]['tieu_de_link'])}
                        {$this->Block->getLocale('tieu_de_link', $data_extend)}
                    {/if}
                </a>
            {/if}
        </div>

        <div class="list">
            <div class="row">
                {foreach from = $data_extend.data_collection[{LANGUAGE}] key = key item = item}
                    <div class="col-lg-4 col-md-6 col-12">
                        <div class="item">
                            <div class="inter-top">
                                <div class="img">
                                    {if !empty($item.logo)}
                                        {assign var = url_img value = "{CDN_URL}{$item.logo}"}
                                    {else}
                                        {assign var = url_img value = "data:image/gif;base64,R0lGODlhAQABAIAAAMLCwgAAACH5BAAAAAAALAAAAAABAAEAAAICRAEAOw=="}
                                    {/if}
                                
                                    {$this->LazyLoad->renderImage([
                                        'src' => $url_img, 
                                        'alt' => "{if !empty($item.name)}{$item.name}{/if}", 
                                        'class' => 'img-fluid'
                                    ])}
                                </div>
                                {if !empty($item.name)}
                                    <div class="name">
                                        {$item.name}
                                    </div>
                                {/if}
                            </div>
                            <div class="inter-content">
                                {if !empty($item.dsc1)}
                                    <div class="dsc">
                                        {$item.dsc1}
                                    </div>
                                {/if}
                                {if !empty($item.dsc2)}
                                    <div class="dsc">
                                        {$item.dsc2}
                                    </div>
                                {/if}
                            </div>
                            <div class="inter-btn">
                                {if !empty($item.urldl)}
                                    <div class="btn-deal btn-left">
                                        <a href="{$item.urldl}" title="{if !empty($item.name)}{$item.name}{/if}">
                                            Show More
                                        </a>
                                    </div>
                                {/if}
                                {if !empty($item.urlour)}
                                    <div class="btn-deal btn-left">
                                        <a href="{$item.urlour}" title="{if !empty($item.name)}{$item.name}{/if}">
                                            Sign Up
                                        </a>
                                    </div>
                                {/if}
                            </div>
                            
                        </div>
                    </div>
                {/foreach}
            </div>
        </div>
    </div>
{/if}"}

